mapper.select:
select 

from wx_menu
where menu_id = #{menuId,jdbcType=BIGINT}
  and menu_name = #{menuName,jdbcType=VARCHAR}

mapper.delete:
delete from wx_menu
where menu_id = #{menuId,jdbcType=BIGINT}
  and menu_name = #{menuName,jdbcType=VARCHAR}

mapper.select:
select

from wx_menu
where menu_name=#{menuName} and parent_id = #{parentId}
